  protected final JPanel canvas = new JPanel(new GridLayout(HEIGHT, WIDTH), true);
  private int row = 1;
  private int col = 1;
  private Direction direction = Direction.east;
  private final Cell[][] world = new Cell[HEIGHT][WIDTH];
